// Get ALL input data from the previous node.
// This will be an array where each element is an n8n item object { json: {...} }
const allInputItems = $input.all();

// Now, transform these n8n item objects into just their 'json' payload
// This results in an array of your raw user objects: [ {user1}, {user2}, ... ]
const users = allInputItems.map(item => item.json);

// The rest of your filtering and mapping logic remains largely the same
// It will now operate on the complete 'users' array

const subscribers = users.filter(user =>
  user.subscribed === true &&
  user.location &&
  user.lat &&
  user.lon
);

// Map the filtered subscribers to the desired output format
// The .map function for 'subscribers' will produce an array of new JSON objects
return subscribers.map(user => ({
  json: {
    userId: user.user_id,
    location: user.location,
    lat: user.lat,
    lon: user.lon,
    chatId: user.user_id // Assuming userId is also used as chatId for private chats
  }
}));

